<?phpnamespace App\Http\Requests\Admin\UserCrudController;use App\Enums\UserGenderEnum;use App\Models\User;use Illuminate\Foundation\Http\FormRequest;use Illuminate\Validation\Rule;class UpdateUserWithProfileRequest extends FormRequest{    public function rules(): array    {        return [            'name' => ['required', 'string', 'max:255'],            'email' => ['required', 'string', 'email', 'max:255', 'unique:' . User::class . ',email,' . $this->id, ],            'gender' => [                'sometimes',                Rule::enum(UserGenderEnum::class),            ],            'birthday' => [                'sometimes',                'date_format:Y-m-d',                Rule::date()->beforeOrEqual(today()->subYears(10)),            ],            'phone' => [                'sometimes',                'nullable',                'phone:RU',                'max:20'            ],            'id' => [                'required',                'integer',                'exists:' . User::class . ',id'            ]        ];    }    public function authorize(): bool    {        return true;    }}